settings.code_cus_css:
.t4s-feature-columns {
  width: 100%;
  max-width: 950px;
  margin: 0 auto;
}

.t4s-feature-columns__content > h3 {
  font-size: 36px !important;
  line-height: 1;
  margin-bottom: 16px;
  font-family: var(--font-heading);
}

.t4s-rte a:not(.btn):not(.t4s-link):not(.t4s-btn):not(.t4s-button):not(.t4s-a) {
  border-bottom: 0;
  padding-bottom: 0;
}

.t4s-feature-columns__content h1,
.t4s-feature-columns__content h2 {
  font-size: 28px !important;
  line-height: 1;
  margin-bottom: 16px;
}
